# PowerShell One Liner Example that reports if anyone is logged into the remote computer or VM
$VM = Read-Host -Prompt 'Input the VM name to check for an interactive login...'; $explorerprocesses = @(Get-WmiObject -ComputerName $VM -Query "Select * FROM Win32_Process WHERE Name='explorer.exe'" -ErrorAction SilentlyContinue); if ($explorerprocesses.Count -eq 0){"No explorer process found / Nobody interactively logged on"} else {foreach ($i in $explorerprocesses){$Username = $i.GetOwner().User; $Domain = $i.GetOwner().Domain; $Domain + "\" + $Username + " logged on since: " + ($i.ConvertToDateTime($i.CreationDate))}}
